import React ,{useState, useEffect}from "react";
import Card from "./Card";
import axios from "axios";

const Main=()=>{
    const [search,setSearch]=useState("");
    const [data,setData]=useState([]);
    const [ sort, setSort] = useState ('');



        const searchBook= async (evt)=>{
            if(evt.key==="Enter" )
            {
              await axios.get('https://www.googleapis.com/books/v1/volumes?q='+search+'&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU'+'&maxResults=40')
                .then((res)=>{
                    const cleanDate = cleanData(res)
                    setData({data: cleanDate})})
                .catch(err=>console.log(err))
                console.log(data);
            }
        }


    const handleSort = (e) => {
        console.log(e.target.value);
        setSort(e.target.value)
    }   

    const cleanData = (data) => {
        const cleanedData = data.body.items.map((book) => {
            if(book.volumeInfo.hasOwnProperty('publishedDate') === false) {
                book.volumeInfo['publishedDate'] === '0000';
            }
            else if(book.volumeInfo.hasOwnProperty('imageLinks') === false) {
                book.volumeInfo['imageLinks'] = {thumbnail:'https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?c'}
            } 
            return book;
        })
        return cleanedData;
    }
    const sortedBooks = data.sort((a, b) => {
        if(sort === 'Newest') {
            return parseInt(b.volumeInfo.publushedDate.substring(0,4)) - parseInt(a.volumeInfo.publushedDate.substring(0,4))
        } else if(sort === 'relevance') {
            return parseInt(a.volumeInfo.publushedDate.substring(0,4)) - parseInt(b.volumeInfo.publushedDate.substring(0,4))
        }
    })

    return(
        <>
            <div className="header">
                <div className="row1">
                    <h1>Google Books Api</h1>
                </div>
                <div className="row2">
                    <h2>Find Your Book</h2>
                    <div className="search">
                        <input type="text" placeholder="Enter Your Book Name"
                        value={search} onChange={e=>setSearch(e.target.value)}
                        onKeyPress={searchBook}/>
                        <button onClick={ searchBook}><i className="fas fa-search"></i></button>
                    </div>
                    {/* <label>categories</label> */}
                {/* <select> 
                    <option></option>
                    <option></option>
                </select> */}
                <label htmlFor='sort'>Sorting by</label>
                <select name='sort' onChange={handleSort} > 
                    <option value='relevance'>relevance</option>
                    <option value='newest' >newest</option>
                </select>
                </div>
            </div>

            <div className="container">
              {
                    <Card book={sortedBooks}/>
              }  
            </div>
        </>
    )
}
export default Main;
