import React ,{useState, useEffect}from "react";
import Card from "./Card";
import axios from "axios";

const Main=()=>{
    const [search,setSearch]=useState("");
    const [bookData,setData]=useState([]);
   useEffect(() => {
       const searchBook= async (evt)=>{
           if(evt.key==="Enter" )
           {
             await axios.get('https://www.googleapis.com/books/v1/volumes?q='+search+'&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU'+'&maxResults=40')
               .then(res=>setData(res.data.items))
               .catch(err=>console.log(err))
           }else{
               await axios.get('https://www.googleapis.com/books/v1/volumes?q='+search+'&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU'+'&maxResults=40')
               .then(res=>setData(res.data.items))
               .catch(err=>console.log(err))
           }
       }

   }, [search])
    const sortingBook= (e)=>{

            axios.get('https://www.googleapis.com/books/v1/volumes?q='+search+'orderBy=newest&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU'+'&maxResults=40')
            .then(res=>setData(res.data.items))
            .catch(err=>console.log(err))

    }

    return(
        <>
            <div className="header">
                <div className="row1">
                    <h1>Google Books Api</h1>
                </div>
                <div className="row2">
                    <h2>Find Your Book</h2>
                    <div className="search">
                        <input type="text" placeholder="Enter Your Book Name"
                        value={search} onChange={e=>setSearch(e.target.value)}
                        onKeyPress={searchBook}/>
                        <button onClick={ searchBook}><i className="fas fa-search"></i></button>
                    </div>
                    {/* <label>categories</label> */}
                {/* <select> 
                    <option></option>
                    <option></option>
                </select> */}
                <label htmlFor='sort'>Sorting by</label>
                <select name='sort' onChange={()=> sortingBook(e => e.target.value) } > 
                    <option value='relevance'>relevance</option>
                    <option value='newest' >newest</option>
                </select>
                </div>
            </div>

            <div className="container">
              {
                    <Card book={bookData}/>
              }  
            </div>
        </>
    )
}
export default Main;
