import React ,{useState, useEffect}from "react";
import Card from "./Card";
import axios from "axios";
import Categories from "./Categories";

const Main=()=>{
    const [search, setSearch]= useState("");
    const [menuItems, setMenuItems]= useState("data");
    const [ data, setData]= useState([]);
    const [ sort, setSort] = useState ('relevance');
    const [categories, setCategories] = useState(allCategories);

    const allCategories = ['all', ...new Set(data.map((item) => item.category))]


        const searchBook= async (evt)=>{
            if(evt.key==="Enter" )
            {
                await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${search}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`)
                  .then(res=>setData(res.data.items))
                  .catch(err=>console.error(err))
            }
        }
        const fetchData = async ()=>{
              await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${categories}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`)
                  .then(res=>setData(res.data.items))
                  .catch(err=>console.error(err))
            console.log(data);
        }
        fetchData()

        const filterItems = (category) => {
            if(category === 'all') {
              setMenuItems(data);
              return;
            }
            const newItems = data.filter((item) => item.category === category);
            setMenuItems(newItems);
          };

    const handleSort = (e) => {
        console.log(e.target.value);
        setSort(e.target.value)
    }   



    return(
        <>
            <div className="header">
                <div className="row1">
                    <h1>Google Books Api</h1>
                </div>
                <div className="row2">
                    <h2>Find Your Book</h2>
                    <div className="search">
                        <input type="text" placeholder="Enter Your Book Name"
                        value={search} onChange={e=>setSearch(e.target.value)}
                        onKeyPress={searchBook}/>
                        <button onClick={searchBook}><i className="fas fa-search"></i></button>
                    </div>
                    <Categories filterItems={filterItems} categories={categories}/>
                <label htmlFor='sort'>Sorting by</label>
                <select defaultValue='sort' onChange={handleSort} > 
                    <option value='relevance'>relevance</option>
                    <option value='newest' >newest</option>
                </select>
                </div>
            </div>

            <div className="container">
              {
                    <Card book={data}/>
              }  
            </div>
        </>
    )
}
export default Main;
