import React, { useState, useEffect } from "react";
import Card from "./Card";
import axios from "axios";

const Main = () => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [sort, setSort] = useState("relevance");
  // const [categories, setCategories] = useState([]);

  const searchBook = async (e) => {
    e.preventDefault();
    await axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=${search}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
      )
      .then((res) => {
        setData(res.data.items);
        // setCategories([...data]);
        // console.log(categories);
      })
      .catch((err) => console.error(err));
  };
  const sortFetching = async () => {
    await axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=${search}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
        )
        .then((res) => {
          setData(res.data.items);
          // setCategories([...data]);
          // console.log(categories);
        })
        .catch((err) => console.error(err));
      };
      // useEffect(()=> {
      //   sortFetching()
      // },[sort])

  const handleSort = (e) => {
    console.log(e.target.value);
    setSort(e.target.value);
    sortFetching();
  };

  return (
    <>
      <div className="header">
        <div className="row1">
          <h1>Google Books Api</h1>
        </div>
        <div className="row2">
          <h2>Find Your Book</h2>
          <div className="search">
            <form onSubmit={searchBook}>
              <input
                type="text"
                placeholder="Enter Your Book Name"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button onClick={onsubmit}>
                <i className="fas fa-search"></i>
              </button>
            </form>
          </div>
          <label>categories</label>

          <select>
            <option></option>
          </select>

          <label htmlFor="sort">Sorting by</label>
          <select defaultValue={sort} onChange={handleSort}>
            <option value="relevance">relevance</option>
            <option value="newest">newest</option>
          </select>
        </div>
      </div>

      <div className="container">{<Card book={data} />}</div>
    </>
  );
};
export default Main;
