import React, { useState, useEffect, useCallback } from "react";
import Card from "./Card";
import axios from "axios";
import Filter from "./Filter";

const Main = () => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [sort, setSort] = useState("relevance");
  // const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchingData = async () => {
      await axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=all&orderBy=revelance&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
        )
        .then((res) => {
          setData(res.data.items);
        })
        .catch((err) => console.error(err));
    };
    fetchingData();
  }, [sort]);

  useEffect(() => {
    const searchBook = async () => {
      await axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=${search}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
        )
        .then((res) => {
          setData(res.data.items);
        })
        .catch((err) => console.error(err));
    };
    searchBook();
  }, [search, sort]);
  const seachItem = (e) => {
    e.preventDefault();
    setSearch(e.target.value);
  };

  useEffect(() => {
    const sortFetching = async () => {
      await axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=${search}&orderBy=${sort}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
        )
        .then((res) => {
          setData(res.data.items);
        })
        .catch((err) => console.error(err));
    };

    sortFetching();
  }, [search, sort]);

  const handleSort = (e) => {
    console.log(e.target.value);
    setSort(e.target.value);
  };

  return (
    <>
      <div className="header">
        <div className="row1">
          <h1>Google Books Api</h1>
        </div>
        <div className="row2">
          <h2>Find Your Book</h2>
          <div className="search">
            <form onSubmit={seachItem}>
              <input
                type="text"
                placeholder="Enter Your Book Name"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button onClick={onsubmit}>
                <i className="fas fa-search"></i>
              </button>
            </form>
          </div>

          <label htmlFor="sort">Sorting by</label>
          <select defaultValue={sort} onChange={handleSort}>
            <option value="relevance">relevance</option>
            <option value="newest">newest</option>
          </select>
        </div>
      </div>

      <div className="container">{<Card book={data} />}</div>
    </>
  );
};
export default Main;
